import React, { useState } from "react";

const Register = () => {
    const [employeeId, setEmployeeId] = useState("");
    const [biometricId, setBiometricId] = useState("");

    const handleRegister = async () => {
        if (!employeeId && !biometricId) {
            alert("Enter either Employee ID or Biometric ID!");
            return;
        }

        const hashedId = await sha256(employeeId || biometricId);
        const did = `did:pramaan:${hashedId.slice(0, 16)}`;

        if (localStorage.getItem(employeeId || biometricId)) {
            alert("Already registered!");
            return;
        }

        localStorage.setItem(employeeId || biometricId, did);
        alert("✅ Successful Registration!");
    };

    return (
        <div className="container">
            <h2>Register</h2>
            <input type="text" placeholder="Enter Employee ID" onChange={(e) => setEmployeeId(e.target.value)} />
            <input type="text" placeholder="Enter Biometric ID" onChange={(e) => setBiometricId(e.target.value)} />
            <button onClick={handleRegister}>Register</button>
        </div>
    );
};

const sha256 = async (message) => {
    const msgBuffer = new TextEncoder().encode(message);
    const hashBuffer = await crypto.subtle.digest("SHA-256", msgBuffer);
    return Array.from(new Uint8Array(hashBuffer)).map(b => b.toString(16).padStart(2, "0")).join("");
};

export default Register;
