/*
 * App.js - Main Routing & Authentication Handling
 */
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Home from "./components/Home";
import Register from "./components/Register";
import Authenticate from "./components/Authenticate";
import ProofHistory from "./components/ProofHistory";
import AdminDashboard from "./components/AdminDashboard";
import Signup from "./components/Signup";
import UserManagement from "./components/UserManagement";
import VerifyRequests from "./components/VerifyRequests";
import "./styles/App.css";
import "./styles/Admin.css";

const App = () => {
    const [userEmail, setUserEmail] = useState(null);

    return (
        <Router>
            <AppContent userEmail={userEmail} setUserEmail={setUserEmail} />
        </Router>
    );
};

const AppContent = ({ userEmail, setUserEmail }) => {
    const handleLogin = () => {
        const enteredEmail = prompt("Enter your Email:");
        if (enteredEmail) {
            setUserEmail(enteredEmail);
            alert("Login successful!");
        }
    };

    const handleLogout = () => {
        setUserEmail(null);
        alert("Logged out successfully.");
    };

    return (
        <>
            <nav className="navbar">
                <h2>Pramaan</h2>
                <div className="nav-links">
                    <Link to="/">Home</Link>
                    <Link to="/register">Register</Link>
                    <Link to="/authenticate">Authentication</Link>
                    <Link to="/proof-history">Proof History</Link>
                </div>
                <div className="auth-buttons">
                    {userEmail ? (
                        <>
                            <span>Logged in as {userEmail === "12214064@nitkkr.ac.in" ? "Admin" : userEmail}</span>
                            <button onClick={handleLogout}>Sign Out</button>
                        </>
                    ) : (
                        <button onClick={handleLogin}>Sign In</button>
                    )}
                </div>
            </nav>

            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/register" element={<Register />} />
                <Route path="/authenticate" element={<Authenticate />} />
                <Route path="/proof-history" element={<ProofHistory />} />
                <Route path="/admin-dashboard" element={<AdminDashboard />} />
                <Route path="/signup" element={<Signup />} />
                <Route path="/user-management" element={<UserManagement />} />
                <Route path="/verify-requests" element={<VerifyRequests />} />
            </Routes>
        </>
    );
};

export default App;
