import React, { useState } from "react";

const VerifyProof = () => {
    const [proof, setProof] = useState(null);
    const [status, setStatus] = useState("");

    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const jsonProof = JSON.parse(e.target.result);
                setProof(jsonProof);
            } catch (error) {
                alert("Invalid JSON file.");
            }
        };
        reader.readAsText(file);
    };

    const handleVerify = async () => {
        if (!proof) {
            alert("Please upload a proof.json file.");
            return;
        }

        // Extract user ID from proof JSON
        const userId = proof.inputs ? proof.inputs[0] : null;

        if (!userId) {
            alert("Invalid proof file. Missing user ID.");
            return;
        }

        const response = await fetch("http://localhost:5000/api/verify", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ proof, userId })
        });

        const data = await response.json();
        setStatus(data.success ? "✅ Authentication Success!" : "❌ Authentication Failed!");
    };

    return (
        <div className="container">
            <h2>Verify Proof</h2>
            <input type="file" accept=".json" onChange={handleFileUpload} />
            <button className="verify-btn" onClick={handleVerify}>Verify</button>
            {status && <p className="verification-status">{status}</p>}
        </div>
    );
};

export default VerifyProof;
