import React, { useState } from "react";
import { JsonRpcProvider, Contract, toBigInt } from "ethers";
import verifierABI from "../contract/Verifier.json";

const VerifyProof = ({ proof }) => {
    const [result, setResult] = useState(null);

    const verifyProofOnChain = async () => {
        if (!proof) {
            alert("Please upload a proof file first.");
            return;
        }

        try {
            // Connect to Polygon Mumbai
            const provider = new JsonRpcProvider(process.env.REACT_APP_ALCHEMY_RPC_URL);
            const signer = provider.getSigner();

            // Load the deployed Verifier contract
            const contract = new Contract(process.env.REACT_APP_CONTRACT_ADDRESS, verifierABI, provider);

            // Extract proof elements
            const { proof: { a, b, c }, inputs } = proof;

            // Format the proof for Solidity
            const formattedProof = {
                a: [toBigInt(a[0]), toBigInt(a[1])],
                b: [
                    [toBigInt(b[0][0]), toBigInt(b[0][1])],
                    [toBigInt(b[1][0]), toBigInt(b[1][1])]
                ],
                c: [toBigInt(c[0]), toBigInt(c[1])]
            };
            
            const formattedInputs = inputs.map((i) => toBigInt(i));

            // Call the `verifyTx` function
            const isValid = await contract.verifyTx(formattedProof, formattedInputs);
            
            setResult(isValid ? "✅ Proof is valid!" : "❌ Proof verification failed!");
        } catch (error) {
            console.error(error);
            setResult("⚠️ Error occurred during verification.");
        }
    };

    return (
        <div>
            <button onClick={verifyProofOnChain}>Verify Proof</button>
            {result && <h3>{result}</h3>}
        </div>
    );
};

export default VerifyProof;
