import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const OrganizationLogin = () => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [isSignup, setIsSignup] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const endpoint = isSignup ? '/api/v1/organizations/signup' : '/dashboard/login';
        
        try {
            const response = await fetch(`http://localhost:5000${endpoint}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            
            const data = await response.json();
            
            if (isSignup && data.success) {
                alert(`Save your API Key: ${data.data.apiKey}`);
                setIsSignup(false);
            } else if (!isSignup && data.token) {
                localStorage.setItem('saasToken', data.token);
                navigate('/dashboard');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className="auth-container">
            <h2>{isSignup ? 'üöÄ Create Organization' : 'üè¢ Organization Login'}</h2>
            <form onSubmit={handleSubmit}>
                {isSignup && (
                    <>
                        <input
                            type="text"
                            placeholder="Organization Name"
                            onChange={(e) => setFormData({...formData, name: e.target.value})}
                            required
                        />
                        <input
                            type="text"
                            placeholder="Domain"
                            onChange={(e) => setFormData({...formData, domain: e.target.value})}
                        />
                    </>
                )}
                <input
                    type="email"
                    placeholder="Email"
                    onChange={(e) => setFormData({...formData, email: e.target.value})}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    onChange={(e) => setFormData({...formData, password: e.target.value})}
                    required
                />
                <button type="submit">
                    {isSignup ? 'Create Organization' : 'Login'}
                </button>
            </form>
            <p>
                {isSignup ? 'Already have an organization?' : "Don't have an organization?"}{' '}
                <a href="#" onClick={() => setIsSignup(!isSignup)}>
                    {isSignup ? 'Login' : 'Create one'}
                </a>
            </p>
        </div>
    );
};

export default OrganizationLogin;
