import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";
import Home from "./components/Home";
import Register from "./components/Register";
import Authenticate from "./components/Authenticate";
import ProofHistory from "./components/ProofHistory";
// import AdminDashboard from "./components/AdminDashboard";

const App = () => {
    const [userId, setUserId] = useState(null);
    const navigate = useNavigate();

    const handleLogin = () => {
        const enteredId = prompt("Enter User ID:");
        if (enteredId) {
            setUserId(enteredId);
            alert("Login successful!");
        }
    };

    const handleLogout = () => {
        setUserId(null);
        alert("Logged out successfully.");
        navigate("/");
    };

    return (
        <Router>
            <nav className="navbar">
                <h2>Pramaan</h2>
                <div className="nav-links">
                    <Link to="/">Home</Link>
                    <Link to="/register">Register</Link>
                    <Link to="/authenticate">Authentication</Link>
                    <Link to="/proof-history">Proof History</Link>
                </div>
                <div className="auth-buttons">
                    {userId ? (
                        <>
                            <span>Logged in as {userId === "12214064" ? "Admin" : `User (${userId})`}</span>
                            <button onClick={handleLogout}>Sign Out</button>
                        </>
                    ) : (
                        <button onClick={handleLogin}>Sign In</button>
                    )}
                </div>
            </nav>

            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/register" element={<Register />} />
                <Route path="/authenticate" element={<Authenticate />} />
                <Route path="/proof-history" element={<ProofHistory />} />
                {/* <Route path="/admin-dashboard" element={<AdminDashboard />} /> */}
            </Routes>
        </Router>
    );
};

export default App;
