import React, { useState, useEffect } from "react";

const ProofHistory = () => {
    const [history, setHistory] = useState([]);

    useEffect(() => {
        const storedHistory = JSON.parse(localStorage.getItem("proofHistory")) || [];
        setHistory(storedHistory);
    }, []);

    return (
        <div className="container">
            <h2>Proof History</h2>
            <table>
                <thead>
                    <tr>
                        <th>Date & Time</th>
                        <th>Employee ID</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {history.map((item, index) => (
                        <tr key={index}>
                            <td>{item.date}</td>
                            <td>{item.employeeId}</td>
                            <td>{item.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ProofHistory;
