import React from "react";
import { Link } from "react-router-dom";

const Navbar = ({ user, isAdmin, onLogout }) => {
    return (
        <nav className="navbar">
            <div className="navbar-left">
                <Link to="/" className="brand">Pramaan</Link>
            </div>

            <div className="navbar-links">
                <Link to="/">Home</Link>
                <Link to="/register">Register</Link>
                <Link to="/authenticate">Authenticate</Link>
                <Link to="/verify-proof">Verify Proof</Link>   {/* Added for proof verification */}
                <Link to="/proof-history">Proof History</Link>

                {isAdmin && <Link to="/admin-dashboard">Admin Dashboard</Link>}

                {user ? (
                    <button className="logout-btn" onClick={onLogout}>Logout</button>
                ) : (
                    <Link to="/login">Login</Link>
                )}
            </div>

        </nav>
    );
};

export default Navbar;
