import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Authenticate = ({ userEmail }) => {
    const [proofData, setProofData] = useState(null);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    if (!userEmail) {
        return (
            <div className="auth-container">
                <h2>🔒 Authentication</h2>
                <p>You must be logged in to authenticate.</p>
                <button onClick={() => navigate("/login")}>Go to Login</button>
            </div>
        );
    }

    const handleAuthenticate = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/authenticate", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email: userEmail }),
            });

            const data = await response.json();
            if (!response.ok) {
                setError(`⚠️ ${data.error}`);
                return;
            }

            setProofData(data.proof);
        } catch (err) {
            setError("⚠️ Authentication failed. Try again.");
        }
    };

    const downloadProof = () => {
        const element = document.createElement("a");
        const file = new Blob([JSON.stringify(proofData, null, 2)], { type: "application/json" });
        element.href = URL.createObjectURL(file);
        element.download = "proof.json";
        document.body.appendChild(element);
        element.click();
    };

    return (
        <div className="auth-container">
            <h2>🔑 Authenticate</h2>
            <p>Authenticated as: <strong>{userEmail}</strong></p>
            <button onClick={handleAuthenticate}>Generate Proof</button>
            {error && <p className="error">{error}</p>}
            {proofData && (
                <button onClick={downloadProof}>Download Proof</button>
            )}
        </div>
    );
};

export default Authenticate;
