import React, { useEffect, useState } from "react";

const ProofHistory = ({ email }) => {
    const [history, setHistory] = useState([]);

    useEffect(() => {
        const fetchHistory = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/proof-history?email=${email}`);
                const data = await response.json();
                setHistory(data);
            } catch (error) {
                console.error("Error fetching proof history:", error);
            }
        };

        if (email) fetchHistory();
    }, [email]);

    return (
        <div className="proof-history">
            <h2>Proof History</h2>
            <table>
                <thead>
                    <tr>
                        <th>Sr. No.</th>
                        <th>Date & Time</th>
                        <th>Verification Status</th>
                    </tr>
                </thead>
                <tbody>
                    {history.map((entry, index) => (
                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{new Date(entry.date).toLocaleString()}</td>
                            <td>{entry.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ProofHistory;
