import React, { useState } from "react";

const VerifyProof = () => {
    const [proof, setProof] = useState(null);
    const [status, setStatus] = useState("");

    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.onload = (e) => setProof(JSON.parse(e.target.result));
        reader.readAsText(file);
    };

    const handleVerify = async () => {
        if (!proof) {
            alert("Upload your proof.json file!");
            return;
        }

        const response = await fetch("http://localhost:5000/api/verify", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ proof, userId: proof.inputs[0] }) // Using stored ID in proof.json
        });

        const data = await response.json();
        setStatus(data.success ? "✅ Authentication Success!" : "❌ Authentication Failed!");
    };

    return (
        <div className="container">
            <h2>Verify Proof</h2>
            <input type="file" accept=".json" onChange={handleFileUpload} />
            <button onClick={handleVerify}>Verify</button>
            {status && <p className="verification-status">{status}</p>}
        </div>
    );
};

export default VerifyProof;
