import React, { useState, useEffect } from 'react';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Copy, Key, Shield, Users, Activity, CreditCard, Settings, Code, AlertCircle, Check } from 'lucide-react';

const SaasDashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [organization, setOrganization] = useState(null);
  const [apiKeys, setApiKeys] = useState([]);
  const [usage, setUsage] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showApiKey, setShowApiKey] = useState(null);
  const [copiedKey, setCopiedKey] = useState(null);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('saasToken');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      const [orgRes, analyticsRes] = await Promise.all([
        fetch('http://localhost:5000/dashboard/organization', { headers }),
        fetch('http://localhost:5000/dashboard/analytics', { headers })
      ]);

      const orgData = await orgRes.json();
      const analyticsData = await analyticsRes.json();

      setOrganization(orgData);
      setApiKeys(orgData.apiKeys || []);
      setUsage(analyticsData);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  const createApiKey = async () => {
    try {
      const token = localStorage.getItem('saasToken');
      const response = await fetch('http://localhost:5000/dashboard/api-keys', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name: `API Key ${new Date().toLocaleDateString()}`,
          testMode: false
        })
      });

      const data = await response.json();
      if (data.success) {
        setShowApiKey(data.apiKey);
        fetchDashboardData();
      }
    } catch (error) {
      console.error('Error creating API key:', error);
    }
  };

  const copyToClipboard = (text, keyId) => {
    navigator.clipboard.writeText(text);
    setCopiedKey(keyId);
    setTimeout(() => setCopiedKey(null), 2000);
  };

  const revokeApiKey = async (keyId) => {
    if (!window.confirm('Are you sure you want to revoke this API key?')) return;

    try {
      const token = localStorage.getItem('saasToken');
      await fetch(`http://localhost:5000/dashboard/api-keys/${keyId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      fetchDashboardData();
    } catch (error) {
      console.error('Error revoking API key:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header */}
      <div className="bg-gray-800 border-b border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-purple-400 to-pink-600 bg-clip-text text-transparent">
                Pramaan SAAS Dashboard
              </h1>
              <p className="text-gray-400 mt-1">{organization?.name}</p>
            </div>
            <div className="flex items-center gap-4">
              <div className="text-right">
                <p className="text-sm text-gray-400">Current Plan</p>
                <p className="text-lg font-semibold capitalize">{organization?.plan}</p>
              </div>
              <button className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-lg transition-colors">
                Upgrade Plan
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="bg-gray-800 border-b border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <nav className="flex space-x-8">
            {['overview', 'api-keys', 'usage', 'settings'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`py-4 px-1 border-b-2 transition-colors capitalize ${
                  activeTab === tab
                    ? 'border-purple-500 text-purple-400'
                    : 'border-transparent text-gray-400 hover:text-white'
                }`}
              >
                {tab.replace('-', ' ')}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-gray-800 p-6 rounded-lg border border-gray-700">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-400 text-sm">Total API Calls</p>
                  <p className="text-2xl font-bold mt-1">{usage?.metrics?.totalRequests || 0}</p>
                </div>
                <Activity className="h-8 w-8 text-purple-500" />
              </div>
            </div>
            
            <div className="bg-gray-800 p-6 rounded-lg border border-gray-700">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-400 text-sm">Active Users</p>
                  <p className="text-2xl font-bold mt-1">{organization?.usage?.currentMonthUsers || 0}</p>
                </div>
                <Users className="h-8 w-8 text-blue-500" />
              </div>
            </div>
            
            <div className="bg-gray-800 p-6 rounded-lg border border-gray-700">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-400 text-sm">Success Rate</p>
                  <p className="text-2xl font-bold mt-1">{usage?.metrics?.successRate || 0}%</p>
                </div>
                <Shield className="h-8 w-8 text-green-500" />
              </div>
            </div>
            
            <div className="bg-gray-800 p-6 rounded-lg border border-gray-700">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-400 text-sm">Avg Response Time</p>
                  <p className="text-2xl font-bold mt-1">{Math.round(usage?.metrics?.avgResponseTime || 0)}ms</p>
                </div>
                <Activity className="h-8 w-8 text-yellow-500" />
              </div>
            </div>
          </div>
        )}

        {/* API Keys Tab */}
        {activeTab === 'api-keys' && (
          <div>
            <div className="mb-6 flex justify-between items-center">
              <h2 className="text-2xl font-bold">API Keys</h2>
              <button
                onClick={createApiKey}
                className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-lg transition-colors flex items-center gap-2"
              >
                <Key className="h-4 w-4" />
                Generate New Key
              </button>
            </div>

            {showApiKey && (
              <div className="bg-green-900/20 border border-green-500 p-4 rounded-lg mb-6">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <p className="text-green-400 font-semibold mb-2">New API Key Created!</p>
                    <p className="text-sm text-gray-300 mb-2">Save this key securely. It won't be shown again.</p>
                    <div className="bg-gray-800 p-3 rounded font-mono text-sm break-all">
                      {showApiKey}
                    </div>
                  </div>
                  <button
                    onClick={() => {
                      copyToClipboard(showApiKey, 'new');
                      setShowApiKey(null);
                    }}
                    className="ml-4 text-green-400 hover:text-green-300"
                  >
                    <Copy className="h-5 w-5" />
                  </button>
                </div>
              </div>
            )}

            <div className="space-y-4">
              {apiKeys.map((key) => (
                <div key={key.id} className="bg-gray-800 p-6 rounded-lg border border-gray-700">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <h3 className="font-semibold text-lg">{key.name}</h3>
                      <p className="text-gray-400 text-sm mt-1">
                        Created: {new Date(key.createdAt).toLocaleDateString()}
                      </p>
                      <p className="text-gray-400 text-sm">
                        Last used: {key.lastUsed ? new Date(key.lastUsed).toLocaleDateString() : 'Never'}
                      </p>
                      <div className="mt-3 flex items-center gap-4">
                        <span className={`px-2 py-1 rounded text-xs ${
                          key.isActive ? 'bg-green-900/30 text-green-400' : 'bg-red-900/30 text-red-400'
                        }`}>
                          {key.isActive ? 'Active' : 'Revoked'}
                        </span>
                        <span className="text-gray-400 text-sm">
                          {key.permissions.maxProofsPerMonth} proofs/month
                        </span>
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      {copiedKey === key.id && (
                        <span className="text-green-400 text-sm flex items-center gap-1">
                          <Check className="h-4 w-4" /> Copied!
                        </span>
                      )}
                      <button
                        onClick={() => revokeApiKey(key.id)}
                        className="text-red-400 hover:text-red-300 transition-colors"
                        disabled={!key.isActive}
                      >
                        Revoke
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Usage Tab */}
        {activeTab === 'usage' && usage && (
          <div>
            <h2 className="text-2xl font-bold mb-6">Usage Analytics</h2>
            
            <div className="bg-gray-800 p-6 rounded-lg border border-gray-700 mb-6">
              <h3 className="text-lg font-semibold mb-4">Monthly API Calls</h3>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={usage.usage}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                  <XAxis dataKey="month" stroke="#9CA3AF" />
                  <YAxis stroke="#9CA3AF" />
                  <Tooltip 
                    contentStyle={{ backgroundColor: '#1F2937', border: '1px solid #374151' }}
                    labelStyle={{ color: '#9CA3AF' }}
                  />
                  <Legend />
                  <Line type="monotone" dataKey="total" stroke="#8B5CF6" strokeWidth={2} />
                  <Line type="monotone" dataKey="authenticate" stroke="#3B82F6" strokeWidth={2} />
                  <Line type="monotone" dataKey="verify" stroke="#10B981" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="bg-gray-800 p-6 rounded-lg border border-gray-700">
                <h3 className="text-lg font-semibold mb-4">Usage Limits</h3>
                <div className="space-y-3">
                  <div>
                    <div className="flex justify-between text-sm mb-1">
                      <span>Monthly Proofs</span>
                      <span>{organization?.usage?.currentMonthProofs || 0} / {apiKeys[0]?.permissions?.maxProofsPerMonth || 1000}</span>
                    </div>
                    <div className="w-full bg-gray-700 rounded-full h-2">
                      <div 
                        className="bg-purple-600 h-2 rounded-full"
                        style={{ width: `${((organization?.usage?.currentMonthProofs || 0) / (apiKeys[0]?.permissions?.maxProofsPerMonth || 1000)) * 100}%` }}
                      />
                    </div>
                  </div>
                  <div>
                    <div className="flex justify-between text-sm mb-1">
                      <span>Active Users</span>
                      <span>{organization?.usage?.currentMonthUsers || 0} / {apiKeys[0]?.permissions?.maxUsers || 100}</span>
                    </div>
                    <div className="w-full bg-gray-700 rounded-full h-2">
                      <div 
                        className="bg-blue-600 h-2 rounded-full"
                        style={{ width: `${((organization?.usage?.currentMonthUsers || 0) / (apiKeys[0]?.permissions?.maxUsers || 100)) * 100}%` }}
                      />
                    </div>
                  </div>
                </div>
              </div>

              <div className="bg-gray-800 p-6 rounded-lg border border-gray-700">
                <h3 className="text-lg font-semibold mb-4">Quick Integration</h3>
                <div className="space-y-3">
                  <div className="bg-gray-900 p-3 rounded">
                    <p className="text-xs text-gray-400 mb-1">JavaScript</p>
                    <code className="text-sm text-purple-400">
                      npm install @pramaan/sdk
                    </code>
                  </div>
                  <div className="bg-gray-900 p-3 rounded">
                    <p className="text-xs text-gray-400 mb-1">Python</p>
                    <code className="text-sm text-purple-400">
                      pip install pramaan-sdk
                    </code>
                  </div>
                  <a 
                    href="/docs"
                    className="inline-flex items-center gap-2 text-purple-400 hover:text-purple-300 text-sm mt-2"
                  >
                    <Code className="h-4 w-4" />
                    View API Documentation
                  </a>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Settings Tab */}
        {activeTab === 'settings' && (
          <div className="max-w-2xl">
            <h2 className="text-2xl font-bold mb-6">Organization Settings</h2>
            
            <div className="bg-gray-800 p-6 rounded-lg border border-gray-700 space-y-6">
              <div>
                <label className="block text-sm font-medium mb-2">Webhook URL</label>
                <input
                  type="url"
                  className="w-full bg-gray-900 border border-gray-700 rounded-lg px-4 py-2 focus:outline-none focus:border-purple-500"
                  placeholder="https://your-app.com/webhook"
                  defaultValue={organization?.settings?.webhookUrl}
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Allowed Origins (CORS)</label>
                <textarea
                  className="w-full bg-gray-900 border border-gray-700 rounded-lg px-4 py-2 focus:outline-none focus:border-purple-500"
                  rows="3"
                  placeholder="https://app.example.com"
                  defaultValue={organization?.settings?.allowedOrigins?.join('\n')}
                />
                <p className="text-xs text-gray-400 mt-1">One origin per line</p>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">IP Whitelist</label>
                <textarea
                  className="w-full bg-gray-900 border border-gray-700 rounded-lg px-4 py-2 focus:outline-none focus:border-purple-500"
                  rows="3"
                  placeholder="192.168.1.1"
                  defaultValue={organization?.settings?.ipWhitelist?.join('\n')}
                />
                <p className="text-xs text-gray-400 mt-1">One IP address per line (leave empty to allow all)</p>
              </div>

              <button className="bg-purple-600 hover:bg-purple-700 px-6 py-2 rounded-lg transition-colors">
                Save Settings
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SaasDashboard;
