import React, { useState } from "react";

const VerifyProof = () => {
    const [proof, setProof] = useState(null);
    const [status, setStatus] = useState("");

    // Handles proof.json file upload
    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();

        reader.onload = (e) => {
            try {
                const jsonProof = JSON.parse(e.target.result);

                // Validate if proof contains "inputs"
                if (!jsonProof.inputs || !Array.isArray(jsonProof.inputs) || jsonProof.inputs.length === 0) {
                    throw new Error("Invalid proof file: Missing inputs");
                }

                setProof(jsonProof);
            } catch (error) {
                alert("Invalid JSON file: " + error.message);
            }
        };
        reader.readAsText(file);
    };

    // Handles Proof Verification on the backend
    const handleVerify = async () => {
        if (!proof) {
            alert("Please upload a proof.json file.");
            return;
        }

        // Extract userId from proof inputs (first input)
        const userId = proof.inputs[0];

        if (!userId) {
            alert("Invalid proof file. Missing user ID.");
            return;
        }

        console.log(`üîç Extracted User ID for Verification: ${userId}`);

        try {
            console.log(`üì§ Sending verification request for User ID: ${userId}`);

            const response = await fetch("http://localhost:5000/api/verify", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ proof, userId })  // ‚úÖ Send userId correctly
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || "Verification Failed");
            }

            const data = await response.json();
            setStatus(data.success ? "‚úÖ Authentication Success!" : "‚ùå Authentication Failed!");
        } catch (error) {
            console.error("‚ùå Error during verification:", error);
            setStatus(`‚ùå Verification Failed: ${error.message}`);
        }
    };


    return (
        <div className="verify-proof-container">
            <h2>Verify Proof</h2>
            <input type="file" accept=".json" onChange={handleFileUpload} />
            <button className="verify-btn" onClick={handleVerify}>Verify</button>
            {status && <p className="verification-status">{status}</p>}
        </div>
    );
};

export default VerifyProof;
