import React, { useState } from "react";
import { verifyProofOnChain } from "../utils/generateProof";

const Authenticate = () => {
    const [proof, setProof] = useState(null);

    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.onload = (e) => setProof(JSON.parse(e.target.result));
        reader.readAsText(file);
    };

    const handleAuthentication = async () => {
        if (!proof) {
            alert("Upload your proof.json file!");
            return;
        }

        const result = await verifyProofOnChain(proof);
        alert(result ? "✅ Authentication Success!" : "❌ Authentication Failed!");
    };

    return (
        <div className="container">
            <h2>Authenticate</h2>
            <input type="file" accept=".json" onChange={handleFileUpload} />
            <button onClick={handleAuthentication}>Authenticate</button>
        </div>
    );
};

export default Authenticate;
