import React, { useState, useEffect } from "react";
import "../styles/App.css";

const ProofHistory = () => {
    const [history, setHistory] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/proof-history")
            .then((response) => response.json())
            .then((data) => setHistory(data))
            .catch((error) => console.error("Error fetching proof history:", error));
    }, []);

    return (
        <div className="proof-history-container">
            <h2>Proof Verification History</h2>
            <table className="history-table">
                <thead>
                    <tr>
                        <th>Sr. No.</th>
                        <th>Date & Time</th>
                        <th>Verification Status</th>
                    </tr>
                </thead>
                <tbody>
                    {history.length === 0 ? (
                        <tr>
                            <td colSpan="3" className="no-data">No verification history found</td>
                        </tr>
                    ) : (
                        history.map((entry, index) => (
                            <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{new Date(entry.timestamp).toLocaleString()}</td>
                                <td className={entry.status === "Success" ? "success" : "failure"}>
                                    {entry.status}
                                </td>
                            </tr>
                        ))
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ProofHistory;
